(12,June,2016), Date(18,September,2016), Date(8,October,2016), Date(9,October,2016), // 2017 Date(22,January,2017), Date(4,February,2017), Date(1,April,2017), Date(27,May,2017), Date(30,September,2017), // 2018 Date(11, February, 2018), Date(24, February, 2018), Date(8, April, 2018), Date(28, April, 2018), Date(29, September, 2018), Date(30, September, 2018), Date(29, December, 2018), // 2019 Date(2, February, 2019), Date(3, February, 2019), Date(28, April, 2019), Date(5, May, 2019), Date(29, September, 2019), Date(12, October, 2019), // 2020 Date(19, January, 2020), Date(26, April, 2020), Date(9, May, 2020), Date(28, June, 2020), Date(27, September, 2020), Date(10, October, 2020), // 2021 Date(7, February, 2021), Date(20, February, 2021), Date(25, April, 2021), Date(8, May, 2021), Date(18, September, 2021), Date(26, September, 2021), Date(9, October, 2021) }; static const Size n = sizeof(working_weekends)/sizeof(working_weekends[0]); static const std::set<Date> workingWeekends(working_weekends+0, working_weekends+n);
// If it is already a SSE business day, it must be a IB business day return sseImpl->isBusinessDay(date) || (workingWeekends.find(date) != workingWeekends.end()); }
}

