    //! %Fed Funds rate fixed by the FED.
    /*! (for balances held at the Federal Reserve) */
    class FedFunds : public OvernightIndex {
      public:
        explicit FedFunds(const Handle<YieldTermStructure>& h =
                                                Handle<YieldTermStructure>());
    };

}


