    */
    class GammaFunction {
      public:
        typedef Real argument_type;
        typedef Real result_type;

        Real value(Real x) const;
        Real logValue(Real x) const;
      private:
        static const Real c1_, c2_, c3_, c4_, c5_, c6_;
    };

